INSERT INTO `cms_templates` VALUES (1,2,2,3,'CMS::Builtin::Page',NULL,'New Application','applications/new','<div class=\"row\">\n  <div class=\"col-md-9\">\n    <div class=\"panel panel-default\">\n      <div class=\"panel-heading\">\n        New Application\n      </div>\n      <div class=\"panel-body panel-footer\">\n        {% form \'application.create\', application, class: \"form-horizontal\" %}\n          {% include \'applications/form\' %}\n          <fieldset>\n            <div class=\"form-group\">\n              <div class=\"col-md-10\">\n                <input class=\"btn btn-primary pull-right\"\n                                 name=\"commit\"\n                                 type=\"submit\"\n                                 value=\"Create Application\" />\n              </div>\n            </div>\n          </fieldset>\n        {% endform %}\n      </div>\n    </div>\n  </div>\n</div>',NULL,NULL,NULL,'2018-02-01 14:43:58','2018-02-01 14:43:58',NULL,NULL,'',NULL,0,'applications/new'),(2,2,2,3,'CMS::Builtin::Page',NULL,'Choose Service','applications/choose_service','<h2>Select service</h2>\n<p>In order to create an application, select one of the services you are subscribed to.</p>\n\n<dl class=\"select-service-list\">\n  {% for service in services %}\n    <dt class=\"service\">\n      <a href=\"{{ urls.new_application }}?service_id={{ service.system_name }}\" class=\"\">{{ service.name }}</a>\n    </dt>\n    <dd>{{ service.description }}</dd>\n  {% endfor %}\n</dl>\n',NULL,NULL,NULL,'2018-02-01 14:44:01','2018-02-01 14:44:01',NULL,NULL,'',NULL,0,'applications/choose_service'),(3,2,2,4,'CMS::Builtin::Page',NULL,'Subscribe to a service','services/new','<h2>Subscribe to Service</h2>\n\n{% form \'subscription.create\' %}\n  <p>Please choose a service and plan you which to subscribe to.</p>\n\n  <fieldset class=\"inputs\" name=\"\">\n    <ol>\n      <li class=\"plan_selector required\" id=\"service_contract_plan_input\">\n        <label for=\"service_contract_plan_id\">Plan<abbr title=\"required\">*</abbr></label>\n        <select id=\"service_contract_plan_id\" name=\"service_contract[plan_id]\">\n            {% if subscription.service %}\n               <optgroup label=\"{{ subscription.service.name }}\">\n                 {% for plan in subscription.service.service_plans %}\n                   <option value=\"{{ plan.id }}\">{{ plan.name }}</option>\n                 {% endfor %}\n               </optgroup>\n            {% else %}\n              {% for service in provider.services %}\n                <optgroup label=\"{{ service.name }}\">\n                  {% for plan in service.service_plans %}\n                    <option value=\"{{ plan.id }}\">{{ plan.name }}</option>\n                  {% endfor %}\n                </optgroup>\n              {% endfor %}\n            {% endif %}\n        </select>\n        <p class=\"inline-hints\">Choose a plan you want to subscribe to.</p>\n      </li>\n    </ol>\n  </fieldset>\n\n  {% include \'service_subscription_licence\' %}\n\n  <fieldset class=\"buttons\">\n    <ol>\n      <li class=\"commit\">\n        <input class=\"important-button create\" name=\"commit\" type=\"submit\" value=\"Subscribe\">\n      </li>\n    </ol>\n  </fieldset>\n\n{% endform %}\n',NULL,NULL,NULL,'2018-02-01 14:44:01','2018-02-01 14:44:01',NULL,NULL,'',NULL,0,'services/new'),(4,2,2,4,'CMS::Builtin::Page',NULL,'List services','services/index','<div class=\"full\">\n  <div class=\"container\" >\n    <div class=\"row\">\n      <div class=\"col-md-10\">\n        <div class=\"panel panel-default\">\n          <div class=\"panel-body\">\n            <table class=\"table\">\n              <thead>\n                <tr>\n                  <th style=\"width:20%\">Name</th>\n                  <th>Description</th>\n                  <th>Plan</th>\n                  <th></th>\n                </tr>\n              </thead>\n              <tbody>\n                {% for service in provider.services %}\n                  <tr class=\"service\">\n                    {% if service.subscribable? %}\n                      {% assign subscription = service.subscription %}\n                      <td>\n                        {{ service.name }}\n                      </td>\n                      <td>\n                        <p>\n                          {{ service.description }}\n                        </p>\n                      </td>\n\n                      <td>\n                        {% if subscription %}\n                          {{ subscription.plan.name  }}\n                          {% unless subscription.live? %}\n                            ({{ subscription.state }})\n                          {% endunless %}\n                        {% endif %}\n                      </td>\n\n                      <td>\n                        {% if subscription %}\n                            {% if subscription.can.change_plan? %}\n                               <a href=\"#\" id=\"choose-plan-{{subscription.id}}\" class=\"btn btn-default\">Review/Change</a>\n\n                               {{ \"plans_widget.js\" | javascript_include_tag }}\n                               {{ \"plans_widget.css\" | stylesheet_link_tag }}\n\n                                <script type=\"text/javascript\">\n                                  //<![CDATA[\n                                  $(document).ready(function() {\n                                    $(\"#choose-plan-{{ subscription.id }}\").click(function(){\n                                      var planID = \'{{ subscription.plan.id }}\';\n                                      var contractID = \'{{ subscription.id }}\';\n                                      var url = \'{{ subscription.change_plan_url }}\';\n\n                                      function plan_chosen_callback(name, planID){\n                                      }\n\n                                      PlanWidget.loadPreview(planID, plan_chosen_callback, url, contractID );\n\n                                      return false;\n                                    });\n                                  });\n                                  //]]>\n                                </script>\n\n                            {% endif %}\n                       {% else %}\n                         {{ \"Subscribe to \" | append: service.name | link_to: service.subscribe_url }}\n                       {% endif %}\n                      </td>\n                      {% endif %}\n                  </tr>\n                 {% endfor %}\n              </tbody>\n            </table>\n          </div>\n        </div>\n      </div>\n    </div>\n  </div>\n</div>\n\n',NULL,NULL,NULL,'2018-02-01 14:44:01','2018-02-01 14:44:01',NULL,NULL,'',NULL,0,'services/index'),(5,2,2,NULL,'CMS::Layout',NULL,'Main layout','main_layout','<!DOCTYPE html>\n<html lang=\"en\">\n  <head>\n    <title>\n      {% if page.title %}\n        {{ page.title }}\n      {% else %}\n        {{ provider.name }}\n      {% endif %}\n    </title>\n\n    <meta charset=\"utf-8\">\n    <meta name=\"viewport\" content=\"width=device-width, initial-scale=1.0\">\n\n    {% csrf %}\n    {{ \'/css/bootstrap.css\' | stylesheet_link_tag }}\n    {{ \'//netdna.bootstrapcdn.com/font-awesome/4.3.0/css/font-awesome.css\' | stylesheet_link_tag }}\n\n    {{ \'//ajax.googleapis.com/ajax/libs/jquery/1.7.1/jquery.min.js\' | javascript_include_tag }}\n    {{ \'/javascripts/bootstrap.min.js\' | javascript_include_tag }}\n    {{ \'/javascripts/3scale.js\' | javascript_include_tag }}\n    {{ \'/javascripts/excanvas.compiled.js\' | javascript_include_tag }}\n\n    <!--[if lt IE 9]>\n      <script src=\"//cdnjs.cloudflare.com/ajax/libs/html5shiv/3.6/html5shiv.min.js\"></script>\n      <script src=\"//cdnjs.cloudflare.com/ajax/libs/respond.js/1.3.0/respond.min.js\"></script>\n    <![endif]-->\n\n    {{ content_of.stylesheets | html_safe }}\n    {{ \'/css/default.css\' | stylesheet_link_tag }}\n\n    {{ content_of.javascripts | html_safe }}\n  </head>\n\n  <body>\n    <header role=\"banner\">\n      <nav class=\"navbar navbar-fixed-top navbar-inverse\" role=\"navigation\">\n        <div class=\"container tabbed\">\n          <div class=\"navbar-header\">\n            <button type=\"button\" class=\"navbar-toggle collapsed\" data-toggle=\"collapse\" data-target=\"#navbar-1\">\n              <span class=\"sr-only\">Toggle navigation</span>\n              <span class=\"icon-bar\"></span>\n              <span class=\"icon-bar\"></span>\n              <span class=\"icon-bar\"></span>\n            </button>\n            <a class=\"navbar-brand\" href=\"/\">{{  provider.name }}</a>\n          </div>\n          {% include \'submenu\'%}\n        </div>\n      </nav>\n\n      {% comment %}\n        WARNING: Please note that if you remove the block below\n        you developer portal will stop displaying errors and\n        information messages such as \'Invalid password\' on\n        login form or similar.\n      {% endcomment %}\n\n        <div id=\"flash-messages\">\n          {% for message in flash.messages %}\n            <div class=\"navbar navbar-fixed-top navbar-default alert alert-{{ message.type }}\" data-dismiss=\"alert\">\n              <div class=\"container\">\n                  <button type=\"button\" class=\"close\"  aria-hidden=\"true\">&times;</button>\n                  {{ message.text }}\n              </div>\n            </div>\n          {% endfor %}\n        </div>\n    </header>\n\n    <main id=\"main-content\" role=\"main\">\n      {% if request.path == \'/\' %}\n        {% content %}\n      {% else %}\n        <div class=\"full\">\n          <div class=\"container\">\n            {% content %}\n          </div>\n        </div>\n      {% endif %}\n    </main>\n\n    <footer role=\"contentinfo\">\n      <div class=\"container\">\n        <ul class=\"list-inline pull-right\">\n          <li>&copy; {{ today.year }} {{ provider.name }},</li>\n          <li><a class=\"powered-by\" href=\"http://3scale.net/\">Powered by 3scale</a></li>\n        </ul>\n      </div>\n    </footer>\n\n    <!-- JS and analytics only. -->\n    <script src=\"//netdna.bootstrapcdn.com/bootstrap/3.0.0/js/bootstrap.min.js\"></script>\n    {% include \'analytics\' %}\n  </body>\n</html>\n',NULL,1,NULL,'2018-02-01 14:44:14','2018-02-01 14:44:14',NULL,NULL,'',NULL,0,'layouts/main_layout'),(6,2,2,NULL,'CMS::Layout',NULL,'Error layout','error','<!DOCTYPE html>\n<html lang=\"en\">\n  <head>\n    <meta charset=\"utf-8\" />\n    <title>{{ page.title }}</title>\n\n    <style>\n      /* normalize.css 2012-03-11T12:53 UTC - http://github.com/necolas/normalize.css */\n      /* =============================================================================\n         HTML5 display definitions\n         ========================================================================== */\n      /*\n       * Corrects block display not defined in IE6/7/8/9 & FF3\n       */\n      /* line 21, ../../app/assets/stylesheets/vendor/normalize.scss */\n      article,\n      aside,\n      details,\n      figcaption,\n      figure,\n      footer,\n      header,\n      hgroup,\n      nav,\n      section,\n      summary {\n        display: block;\n      }\n\n      /*\n       * Corrects inline-block display not defined in IE6/7/8/9 & FF3\n       */\n      /* line 31, ../../app/assets/stylesheets/vendor/normalize.scss */\n      audio,\n      canvas,\n      video {\n        display: inline-block;\n        *display: inline;\n        *zoom: 1;\n      }\n\n      /*\n       * Prevents modern browsers from displaying \'audio\' without controls\n       * Remove excess height in iOS5 devices\n       */\n      /* line 42, ../../app/assets/stylesheets/vendor/normalize.scss */\n      audio:not([controls]) {\n        display: none;\n        height: 0;\n      }\n\n      /*\n       * Addresses styling for \'hidden\' attribute not present in IE7/8/9, FF3, S4\n       * Known issue: no IE6 support\n       */\n    