s %}\r\n                          <li>\r\n                            <i class=\"fa fa-check\"></i>\r\n                            {{ feature.name }}\r\n                          </li>\r\n                        {% endfor %}\r\n                      </ul>\r\n                    </div>\r\n                  {% endif %}\r\n\r\n      <!--        <div class=\"col-md-6\">\r\n                    <h5>Limits</h5>\r\n                    <ul class=\"limits list-unstyled\">\r\n                      {% if plan.usage_limits == present %}\r\n                        {% for limit in plan.usage_limits %}\r\n                          <li>\r\n                            <i class=\"fa fa-signal\"></i>\r\n                            {{ limit.metric.name }} &ndash; {{ limit.value }} {{ limit.metric.unit }}s per {{ limit.period }}\r\n                          </li>\r\n                        {% endfor %}\r\n                      {% else %}\r\n                        <li>\r\n                          <i class=\"fa fa-signal\"></i>\r\n                          No limits\r\n                        </li>\r\n                      {% endif %}\r\n                    </ul>\r\n                  </div> -->\r\n\r\n                </div>\r\n              </div>\r\n                <div class=\"panel-footer\">\r\n                <div class=\"row\">\r\n                  <div class=\"col-md-12\">\r\n\r\n                    <a class=\"btn btn-success pull-right\" href=\"{{ urls.signup }}?{{ plan | to_param }}\">Signup to plan {{ plan.name }}</a>\r\n                  </div>\r\n                </div>\r\n\r\n              </div>\r\n            </article>\r\n          </div>\r\n        {% endfor %}\r\n      </div>\r\n    </div>\r\n  </section>\r\n{% endif %}\r\n\r\n<section class=\"invert\">\r\n  <div class=\"container\">\r\n    <h1>Run your requests</h1>\r\n    <div class=\"row\">\r\n\r\n      <div class=\"col-md-12\">\r\n        <h3><code style=\"display:block\">$ curl -v https://echo-api.3scale.net</code></