f provider.multiple_services_allowed? %}\r\n          <li class=\"{% if urls.services.active? %}active{% endif %}\">\r\n            <a href=\"{{ urls.services }}\">{{ urls.services.title }}</a>\r\n          </li>\r\n        {% endif %}\r\n\r\n        <li><a class=\"{% if urls.docs.active? %}active{% endif %}\" href=\"/members\">Members API</a></li>\r\n        <li><a class=\"{% if urls.docs.active? %}active{% endif %}\" href=\"/docs\">Documentation</a></li>\r\n\r\n      </ul>\r\n    {% endunless %}\r\n    <ul id=\"user_widget\" class=\"nav navbar-nav navbar-right\">\r\n      {% unless current_account.requires_credit_card_now? %}\r\n        <li class=\"{% if urls.messages_inbox.active? %}active{% endif %}\">\r\n          <a href=\"{{ urls.messages_inbox }}\">\r\n            Messages\r\n            {% if current_account.unread_messages.size > 0 %}\r\n              <span class=\"badge\">{{ current_account.unread_messages.size }}</span>\r\n            {% endif %}\r\n          </a>\r\n        </li>\r\n        <li class=\"{% if urls.account_overview.active? %}active{% endif %}\">\r\n          <a href=\"{{ urls.account_overview }}\">\r\n            <i class=\"fa fa-cogs\"></i>\r\n            Settings\r\n          </a>\r\n        </li>\r\n      {% endunless %}\r\n      <li>\r\n        <a id=\"sign-out-button\" class=\"pull-right sign-out\" title=\"Logout {{ current_user.username }}\" href=\"{{ urls.logout }}\">\r\n          <i class=\"fa fa-sign-out\"></i>\r\n        </a>\r\n      </li>\r\n    </ul>\r\n\r\n  {% else %}\r\n    <ul class=\"nav navbar-nav\">\r\n      <li><a href=\"/docs\">Documentation</a></li>\r\n      <li><a href=\"/#plans\">Plans</a></li>\r\n    </ul>\r\n    <ul class=\"nav navbar-nav navbar-right\">\r\n      <li>\r\n        <a href=\"{{ urls.login }}\" title=\"Login\">\r\n          Sign in\r\n        </a>\r\n      </li>\r\n    </ul>\r\n  {% endif %}\r\n</div>\r\n',NULL,1,NULL,'2018-02-05 12:05:17','2018-02-05 12:05:17',NULL,21,'CMS::Template',NULL,'admin',NULL,0),(52,2,2,2,'CMS::Template::Version','/','Homepage','','<header class=\"jumbotron page-header\">\r\n  <div class=\"container\">\r\n    <div class=\"row\">\r\n      <div class=\"col-md-12\">\r\n        <h1>Members API</h1>\r\n      </div>\r\n    </div>\r\n  </div>\r\n</header>\r\n\r\n{% if current_user %}\r\n  <section class=\"start\">\r\n  <div class=\"container\">\r\n  <div class=\"row\">\r\n    <div class=\"col-md-6\">\r\n      <h1>Your API Key</h1>\r\n      <br>\r\n      <p class=\"lead\">\r\n       This is your API key that should be kept secret. Use it to authenticate and report the calls you make to the Members API.\r\n      </p>\r\n    </div>\r\n    <div class=\"col-md-6\" style=\"padding-top:2em;\">\r\n      <div class=\"panel panel-default\" id=\"access-details\">\r\n        <div class=\"panel-heading\">\r\n          <i class=\"fa fa-key\"></i>\r\n          Credentials\r\n          {% if current_account.applications.size > 0 %}\r\n            <a class=\"pull-right\" href=\"{{ urls.applications }}\" title=\"Applications\">\r\n              <i class=\"fa fa-chevron-right\"></i>\r\n            </a>\r\n          {% endif %}\r\n        </div>\r\n        <div class=\"panel-body panel-footer\">\r\n          {% if current_account.applications.size == 0 %}\r\n            <a href=\"{{ urls.new_application }}\" class=\"btn btn-primary\">Create Application</a>\r\n          {% elsif current_account.applications.size == 1 %}\r\n            {% assign app = current_account.applications.first %}\r\n\r\n            {% if app.user_key_mode? %}\r\n              <dl class=\"dl-horizontal\">\r\n                <dt>App name</dt>\r\n                <dd><a href=\"{{ app.url }}\">{{ app.name }}</a></dd>\r\n                <dt>Key</dt>\r\n                <dd>\r\n                  <code>{{ app.user_key }}</code>\r\n                  <p>Add this as a <code>user_key</code> parameter to your API calls to authenticate.</p>\r\n                </dd>\r\n              </dl>\r\n\r\n            {% elsif app.app_id_mode? %}\r\n              <dl class=\"dl-horizontal\">\r\n                <dt>App name</dt>\r\n                <dd><a href=\"{{ app.url }}\">{{ app.name }}</a></dd>\r\n                <dt>App ID</dt>\r\n                <dd><code>{{ app.application_id }}</code></dd>\r\n                <dt>Key</dt>\r\n                <dd><code>{{ app.keys.first }}</code></dd>\r\n              </dl>\r\n\r\n            {% elsif app.oauth_mode? %}\r\n              <dl class=\"dl-horizontal\">\r\n                <dt>App name</dt>\r\n                <dd><a href=\"{{ app.url }}\">{{ app.name }}</a></dd>\r\n                <dt>Client ID</dt>\r\n                <dd><code>{{ app.application_id }}</code></dd>\r\n                <dt>Client Secret</dt>\r\n                <dd><code>{{ app.keys.first }}</code></dd>\r\n              </dl>\r\n            {% endif %}\r\n\r\n            {% if app.trial? %}\r\n              <div class=\"alert alert-warning\">\r\n                <h3>Trial period</h3>\r\n                <p>{{ app.remaining_trial_period_days }} days remaining.</p>\r\n              </div>\r\n            {% endif %}\r\n\r\n          {% elsif current_account.applications.size > 1 %}\r\n            <a href=\"{{ urls.applications }}\" class=\"btn btn-primary\">See your Applications & their credentials</a>\r\n          {% endif %}\r\n          </div>\r\n        </div>\r\n      </div>\r\n    </div>\r\n\r\n  </div>\r\n  </section>\r\n\r\n{% else %}\r\n\r\n  <section class=\"sell\">\r\n    <div class=\"container\">\r\n      <div class=\"row\">\r\n        <div class=\"col-md-4\">\r\n          <h3>Register</h3>\r\n          <p>\r\n          <i class=\"fa fa-sign-in fa-3x pull-left\"></i>\r\n          Register to the developer portal to use the Members API\r\n          </p>\r\n        </div>\r\n        <div class=\"col-md-4\">\r\n          <h3>Get your API key</h3>\r\n          <p>\r\n          <i class=\"fa fa-key fa-3x pull-left\"></i>\r\n          Use your API key to authenticate and report the calls you make\r\n          </p>\r\n        </div>\r\n       <div class=\"col-md-4\">\r\n        <h3>Create your app</h3>\r\n         <p>\r\n         <i class=\"fa fa-code fa-3x pull-left\"></i>\r\n         Start coding and create awesome applications with the Members API\r\n         </p>\r\n        </div>\r\n       </div>\r\n      </div>\r\n  </section>\r\n\r\n  <section class=\"plans\" id=\"plans\">\r\n    <div class=\"container\">\r\n      <h1>Pick your plan</h1>\r\n      <br/>\r\n      <div class=\"row\">\r\n        {% for plan in provider.services.Members_Registration_Service21.application_plans %}\r\n          <div class=\"col-md-6\">\r\n            <article class=\"panel panel-default\">\r\n              <div class=\"panel-heading\">\r\n                <strong>{{ plan.name }}</strong>\r\n              </div>\r\n              <div class=\"panel-body\">\r\n                <div class=\"row\">\r\n\r\n                  {% if plan.features == present %}\r\n                    <div class=\"col-md-6\">\r\n                      <h5>Features</h5>\r\n                      <ul class=\"features list-unstyled\">\r\n                        {% for feature in plan.features %}\r\n                          <li>\r\n                            <i class=\"fa fa-check\"></i>\r\n                            {{ feature.name }}\r\n                          </li>\r\n                        {% endfor %}\r\n                      </ul>\r\n                    </div>\r\n                  {% endif %}\r\n\r\n                  <div class=\"col-md-6\">\r\n                    <h5>Limits</h5>\r\n                    <ul class=\"limits list-unstyled\">\r\n                      {% if plan.usage_limits == present %}\r\n                        {% for limit in plan.usage_limits %}\r\n                          <li>\r\n                            <i class=\"fa fa-signal\"></i>\r\n                            {{ limit.metric.name }} &ndash; {{ limit.value }} {{ limit.metric.unit }}s per {{ limit.period }}\r\n                          </li>\r\n                        {% endfor %}\r\n                      {% else %}\r\n                        <li>\r\n                          <i class=\"fa fa-signal\"></i>\r\n                          No limits\r\n                        </li>\r\n                      {% endif %}\r\n                    </ul>\r\n                  </div>\r\n\r\n                </div>\r\n              </div>\r\n                <div class=\"panel-footer\">\r\n                <div class=\"row\">\r\n                  <div class=\"col-md-12\">\r\n\r\n                    <a class=\"btn btn-success pull-right\" href=\"{{ urls.signup }}?{{ plan | to_param }}\">Signup to plan {{ plan.name }}</a>\r\n                  </div>\r\n                </div>\r\n\r\n              </div>\r\n            </article>\r\n          </div>\r\n        {% endfor %}\r\n      </div>\r\n    </div>\r\n  </section>\r\n{% endif %}\r\n\r\n<section class=\"invert\">\r\n  <div class=\"container\">\r\n    <h1>Run your requests</h1>\r\n    <div class=\"row\">\r\n\r\n      <div class=\"col-md-12\">\r\n        <h3><code style=\"display:block\">$ curl -v https://echo-api.3scale.net</code></h3>\r\n        <br/>\r\n        <div class=\"row\">\r\n          <div class=\"col-md-6\">\r\n            <div class=\"panel panel-default\">\r\n              <div class=\"panel-heading\">\r\n                Request\r\n              </div>\r\n              <div class=\"panel-body panel-footer\">\r\n                <pre>\r\n&gt; GET / HTTP/1.1\r\n&gt; User-Agent: curl/7.27.0\r\n&gt; Host: https://echo-api.3scale.net/echo\r\n&gt; Accept: */*\r\n&gt;\r\n                </pre>\r\n              </div>\r\n            </div>\r\n          </div>\r\n          <div class=\"col-md-6\">\r\n            <div class=\"panel panel-default\">\r\n              <div class=\"panel-heading\">\r\n                Response\r\n              </div>\r\n              <div class=\"panel-body panel-footer\">\r\n          