type=\"text/template\">\r\n<h1>APIs available</h1>\r\n  <div class=\"row\">\r\n    <div class=\"col-md-8\">\r\n          {{> searchBar}}\r\n          {{> apiList}}\r\n    </div>\r\n    <div class=\"col-md-4\">\r\n      <div class=\"panel panel-default\">\r\n        <div class=\"panel-body\">\r\n          {{> categories}}\r\n        </div> <!-- end of panel-body -->\r\n      </div> <!-- end of panel-default -->\r\n     </div> <!-- end of col-md-4 -->\r\n    </div> <!-- end of col-md-8 -->\r\n  </div>\r\n</script>\r\n\r\n<script id=\"searchBarTpl\" type=\"text/template\">\r\n  <div class = \"panel panel-default\">\r\n    <div class=\"panel-body\">\r\n        <input class=\"xlarge\" id=\"search_input\" name=\"search\" placeholder=\"Search APIs on sms, photo, user etc...\" dir=\"ltr\" spellcheck=\"false\" autofocus=\"\" type=\"text\">\r\n        <button class=\"btn btn-large btn-warning\" id=\"search_submit\" type=\"submit\" value=\"search\"> <i class=\"fa fa-search\" aria-hidden=\"true\"></i></button>\r\n    </div>\r\n  </div>\r\n</script>\r\n\r\n<script id=\"apiListTpl\" type=\"text/template\">\r\n  <div id=\"apilist\">\r\n    {{#each apis}}\r\n     <a href=\"/apidetails?service_id={{X-3scale.service_id}}\">\r\n        <div class = \"panel panel-default\">\r\n          <div class=\"panel-body\">\r\n            <div class= \"col-md-2\">  <!-- Comment this div if you don\'t want to show images-->\r\n              {{#if this.image}}\r\n                <img src=\"{{this.image}}\" alt=\"{{this.name}} logo\" class=\"img-responsive center-block\"/>\r\n                {{else}}\r\n                  <img src=\"http://apis.io/img/default.svg\" alt=\"{{this.name}} logo\" class=\"img-responsive center-block\"/>\r\n              {{/if}}\r\n            </div>\r\n            <div class= \"col-md-10\">\r\n              <h2>{{ this.name }}</h2>\r\n              <p>{{ this.description }} </p>\r\n              {{#each this.tags}}\r\n                <span class=\"label label-default\">{{.}}</span>\r\n              {{/each}}\r\n            </div>\r\n          </div>\r\n        </div>\r\n      </a>\r\n    {{/each}}\r\n  </div>\r\n</script>\r\n\r\n<script id=\"categoriesTpl\" type=\"text/template\">\r\n  <div id=\"categories\">\r\n      <h2>Categories</h2>\r\n        <ul class=\"list-group checked-list-box\">\r\n          {{#each categories}}\r\n              <li class=\"list-group-item\" data-style=\"button\" data-color=\"info\"><input type=\"checkbox\" id=\"box{{@index}}\" class=\"categoryCheckbox\" value=\"{{name}}\"> <label for=\"box{{@index}}\">{{ name }} <span class=\"categoryCount\">({{nb}})</span></label></li>\r\n          {{/each}}\r\n        <ul>\r\n  </div>\r\n</script>\r\n\r\n    <script>\r\n    var APIS = []\r\n    var paging_skip = 0\r\n    var paging_limit = 5\r\n    var paging_total = 0\r\n\r\n    $(document).ready(function(){\r\n      Handlebars.registerPartial(\"searchBar\", $(\"#searchBarTpl\").html());\r\n      Handlebars.registerPartial(\"categories\", $(\"#categoriesTpl\").html());\r\n      Handlebars.registerPartial(\"apiList\", $(\"#apiListTpl\").html());\r\n\r\n      $.getJSON(\'apis.json\').done(function(data) {\r\n        APIS = data.apis\r\n        data.categories = getTags(data.apis)\r\n        data.apis = _.first(APIS,paging_limit)\r\n\r\n        console.log(data)\r\n        paging_total = APIS.length\r\n\r\n        console.log(\"DATA APIS\",data.apis.length)\r\n        var template = $(\"#containerTpl\").html();\r\n        var stone = Handlebars.compile(template)(data);\r\n        $(\"#content\").html(stone);\r\n      })\r\n\r\n      $(document).on(\'click\',\"#search_submit\",function(e){\r\n        e.preventDefault();\r\n        var regex = new RegExp($(\"#search_input\").val().trim(), \"i\")\r\n        compileAPIsListTemplate(filterArrayByRegEx(APIS,regex))\r\n      })\r\n\r\n      $( \"#searchBarForm\" ).submit(function( e ) {\r\n        e.preventDefault();\r\n        var regex = new RegExp($(\"#search_input\").val().trim(), \"i\")\r\n        compileAPIsListTemplate(filterArrayByRegEx(APIS,regex))\r\n      });\r\n\r\n      $(document).on(\'keyup\',\"#search_input\",function(event){\r\n        event.preventDefault()\r\n          var regex = new RegExp($(\"#search_input\").val().trim(), \"i\")\r\n          compileAPIsListTemplate(filterArrayByRegEx(APIS,regex))\r\n      })\r\n\r\n      $(document).on(\'click\',\':checkbox\',function(event){\r\n        var tags = $(\':checkbox:checked\').map(function() {\r\n           return $(this).val();\r\n        });\r\n        if(tags.length==0){\r\n          compileAPIsListTemplate(_.first(APIS,paging_limit));\r\n        }else{\r\n          compileAPIsListTemplate(filterArrayByTags(APIS,tags));\r\n        }\r\n      })\r\n    }) //end of document.ready\r\n\r\n    //Triggers when reach end of page\r\n    $(window).scroll(function() {\r\n      if ($(window).scrollTop() + $(window).height() > $(document).height() - 50) {\r\n          incrementLimit();\r\n      }\r\n    });\r\n\r\n    //Filter array when name, or description matche regex\r\n    function filterArrayByRegEx(arr, regex){\r\n      return _.filter(arr, function(obj){ return obj.name.match(regex) || obj.description.match(regex)});\r\n    }\r\n\r\n    function filterArrayByTags(arr, tagsArr){\r\n      return _.filter(arr, function(obj){ return _.intersection(obj.tags,tagsArr).length > 0});\r\n    }\r\n\r\n    //Apply list of APIs to template\r\n    function compileAPIsListTemplate(arr){\r\n      var template = $(\'#apiListTpl\').html();\r\n      var data = {}\r\n      data.apis = arr\r\n      var stone = Handlebars.compile(template)(data);\r\n      $(\'#apilist\').html(stone);\r\n    }\r\n\r\n    function allTags(arr){\r\n      return _.uniq(APIS.reduceRight(function(a, b) {\r\n          return a.concat(b.tags);\r\n      }, [])).sort();\r\n    }\r\n\r\n    function getTags(arr){\r\n      return _.map(_.groupBy(_.flatten(_.pluck(arr, \'tags\')).sort(), function(item){\r\n          return item;\r\n      }),function(value,key){\r\n        return {\r\n          name: key,\r\n          nb: value.length\r\n        }\r\n      });\r\n    }\r\n\r\n    function incrementLimit(){\r\n      if(paging_skip >= (paging_total)){ //reached the limit\r\n        return false\r\n      }else{\r\n        skip = paging_skip + paging_limit\r\n        paging_skip=skip\r\n        compileAPIsListTemplate(_.first(APIS,skip));\r\n      }\r\n    }\r\n    </script>',NULL,0,'text/html','2018-02-07 14:23:53','2018-02-07 14:23:53',5,69,'CMS::Template',NULL,'admin',NULL,1),(63,2,2,2,'CMS::Template::Version','/apidetails','apidetails','','\r\n{% active_docs version: \"2.0\" %}\r\n<div id=\"content\"></div>\r\n\r\n<style media=\"screen\">\r\n  #resources_container{\r\n    padding-bottom: 0;\r\n  }\r\n</style>\r\n\r\n<script id=\"leftmenuTpl\" type=\"text/template\">\r\n{% raw %}\r\n  <h2>{{name}}</h2>\r\n  <hr>\r\n  <ul class=\"nav nav-pills nav-stacked\">\r\n    <li><a href=\"#about\">About</a></li>\r\n    <li><a href=\"#documentation\">Documentation</a></li>\r\n    <li><a href=\"#signup\">Signup</a></li>\r\n    <li><a href=\"#terms\">Terms and Conditions</a></li>\r\n    <li><a href=\"#support\">Support</a></li>\r\n  </ul>\r\n{% endraw %}\r\n</script>\r\n\r\n<script id=\"aboutTpl\" type=\"text/template\">\r\n{% raw %}\r\n  <div id=\"about\">\r\n    <h2>About</h2>\r\n      <ul class=\"list-group checked-list-box\">\r\n        <p>\r\n          {{description}}\r\n        </p>\r\n      <ul>\r\n  </div>\r\n{% endraw %}\r\n</script>\r\n\r\n<script id=\"documentationTpl\" type=\"text/template\">\r\n{% raw %}\r\n  <div id=\"documentation\">\r\n    <h2>Documentation</h2>\r\n      <p id=\"swagger-ui-containers\"></p>\r\n  </div>\r\n{% endraw %}\r\n</script>\r\n\r\n<script id=\"signupTpl\" type=\"text/template\">\r\n{% raw %}\r\n  <div id=\"signup\">\r\n    <h2>Signup</h2>\r\n      <p>\r\n        To be able to consume and test these API\'s, you need to sign up at the developer portal. Some of the services will require authorisation before api keys are issued, therefore please give youself plenty of time. Normally, api keys are issued within 24 hours..\r\n      </p>\r\n  </div>\r\n{% endraw %}\r\n</script>\r\n\r\n<script id=\"termsTpl\" type=\"text/template\">\r\n{% raw %}\r\n  <div id=\"terms\">\r\n    <h2>Terms and Conditions</h2>\r\n      <p>\r\n        Service is used as seen, please fill your boots, and don\'t blame me if it doesn\'t work!!\r\n      </p>\r\n  </div>\r\n{% endraw %}\r\n</script>\r\n\r\n<script id=\"supportTpl\" type=\"text/template\">\r\n{% raw %}\r\n  <div id=\"support\">\r\n    <h2>Support</h2>\r\n      <p>\r\n        You can speak to the support team via the messaging system within the developer portal. For higher level applications, telephone support is available either 5 x 7, or 24 x 7 depending on the plan subscription\r\n      </p>\r\n  </div>\r\n{% endraw %}\r\n</script>\r\n\r\n<script id=\"errorTpl\" type=\"text/template\">\r\n{% raw %}\r\n  <div>\r\n  <div class=\"alert alert-danger\" role=\"alert\">\r\n    <i class=\"fa fa-exclamation-triangle\" aria-hidden=\"true\"></i>\r\n    <span class=\"sr-only\">Error:</span>\r\n        {{message}}\r\n    </div>\r\n  </div>\r\n{% endraw %}\r\n</script>\r\n\r\n\r\n<script id=\"detailsPanelTpl\" type=\"text/template\">\r\n{% raw %}\r\n  {{> about}}\r\n  <hr>\r\n  {{> documentation}}\r\n  <hr>\r\n  {{> signup}}\r\n  <hr>\r\n  {{> terms}}\r\n  <hr>\r\n  {{> support}}\r\n{% endraw %}\r\n</script>\r\n\r\n<script id=\"containerTpl\" type=\"text/template\">\r\n{% raw %}\r\n  <div class=\"row\">\r\n    <div class=\"col-md-3\" style=\"margin-top: 40px;\">\r\n      <div class=\"panel panel-default\">\r\n        <div class=\"panel-body\">\r\n          {{> leftMenu}}\r\n        </div>\r\n      </div>\r\n    </div>\r\n    <div class=\"col-md-8\">\r\n      <h4><a href=\"/apilist\">APIs</a> >> {{name}}</h4>\r\n      <div class=\"panel panel-default\">\r\n        <div class=\"panel-body\">\r\n          <div id=\"rightPanel\"></div>\r\n            {{> rightPanel}}\r\n          </div>\r\n      </div>\r\n    </div> <!-- end of col-md-8 -->\r\n  </div>\r\n{% endraw %}\r\n</script>\r\n\r\n<script>\r\n  var APIS = []\r\n  var API = []\r\n\r\n  $(document).ready(function(){\r\n     Handlebars.registerPartial(\"about\", $(\"#aboutTpl\").html());\r\n     Handlebars.registerPartial(\"documentation\", $(\"#documentationTpl\").html());\r\n     Handlebars.registerPartial(\"signup\", $(\"#signupTpl\").html());\r\n     Handlebars.registerPartial(\"terms\", $(\"#termsTpl\").html());\r\n     Handlebars.registerPartial(\"support\", $(\"#supportTpl\").html());\r\n     Handlebars.registerPartial(\"rightPanel\", $(\"#detailsPanelTpl\").html());\r\n     Handlebars.registerPartial(\"leftMenu\", $(\"#leftmenuTpl\").html());\r\n     Handlebars.registerPartial(\"error\", $(\"#errorTpl\").html());\r\n\r\n    $(\"#swagger-ui-container\").parent().remove();\r\n\r\n    var service_id = Number(extractServiceID())\r\n    var url = \'apis.json\'\r\n//4a.function creation\r\n// var slingshot = function (url, tplId, anchor) {\r\n    $.getJSON(url).done(function(data) {\r\n      console.log(\"DONE\");\r\n      console.log(data)\r\n      APIS=data.apis\r\n\r\n      console.log(\"SERVICE_ID\",service_id)\r\n\r\n      API = filterArraybyThreescaleServiceId(APIS,service_id)\r\n\r\n      if(API.length ==0){\r\n        var template = $(\"#errorTpl\").html();\r\n        var stone = Handlebars.compile(template)({message:\"No API was found.\"});\r\n        $(\"#content\").html(stone);\r\n      }else{\r\n        console.log(\"AAAPI\",API)\r\n        //Display in template\r\n        var template = $(\"#containerTpl\").html();\r\n        var stone = Handlebars.compile(template)(API[0]);\r\n        $(\"#content\").html(stone);\r\n\r\n		var specSystemName = API[0][\'X-3scale\'][\'swagger_system_name\'];\r\n      	var domId = \"swagger-ui-container-\" + specSystemName;\r\n      	loadSwagger(specSystemName, domId);\r\n      }\r\n     });\r\n\r\n  })\r\n\r\n  function filterArraybyThreescaleServiceId(arr,service_id){\r\n    return _.filter(arr, function(obj){ if(typeof obj[\"X-3scale\"] != \"undefined\"){return obj[\"X-3scale\"].service_id === service_id}});\r\n  }\r\n\r\n  function extractServiceID(){\r\n    var url = window.location.href\r\n    var captured = /service_id=([^&]+)/.exec(url)[1]; // Value is in [1]\r\n    return captured;\r\n  }\r\n\r\n  function swaggerSectionHtml(domId) {\r\n    var html = \'<div class=\"swagger-section\"><div id=\"\' + domId + \'\" class=\"swagger-ui-wrap\"></div></div>\'\r\n    return html;\r\n  }\r\n\r\n  function getSwaggerSpecUrl(specSystemName) {\r\n    // check parent location, if it\'s iframe\r\n    var loc = window.location.href == \'about:srcdoc\' ? window.parent.location : window.location;\r\n    var url = loc.protocol+\'//\'+loc.hostname+(loc.port ? \':\'+loc.port : \'\');\r\n    url = url + \"/swagger/spec/\" + specSystemName + \".json\";\r\n    return url;\r\n  }\r\n\r\n  function loadSwagger(specSystemName, domId) {\r\n    // create DOM element\r\n    $(\"#swagger-ui-containers\").append(swaggerSectionHtml(domId));\r\n\r\n    var swaggerObjName = \'swaggerUi\' + specSystemName;\r\n    var domPreCodeId = \'#\' + domId + \' pre code\';\r\n    var swaggerObj = new SwaggerUi({\r\n      url: getSwaggerSpecUrl(specSystemName),\r\n      dom_id: domId,\r\n      supportedSubmitMethods: [\'get\', \'post\', \'put\', \'delete\', \'patch\'],\r\n      onComplete: function(swaggerApi, swaggerUi) {\r\n        $(domPreCodeId).each(function(i, e) {hljs.highlightBlock(e)});\r\n        $(\"#api_info\").toggle(); //hide api info section\r\n        $(\".footer\").toggle(); // hide footer section\r\n        $(\".heading h2, .options\").hide();\r\n      },\r\n      onFailure: function(data) {\r\n        log(\"Unable to LoadSwaggerUI\");\r\n      },\r\n      validatorUrl: null,\r\n      docExpansion: \"list\",\r\n      transport: function(httpClient, obj) {\r\n        return ApiDocsProxy.execute(httpClient, obj);\r\n      }\r\n    });\r\n    window[swaggerObjName] = swaggerObj;\r\n    window[swaggerObjName].load();\r\n  }\r\n</script>',NULL,1,'text/html','2018-02-07 14:26:28','2018-02-07 14:26:28',NULL,70,'CMS::Template',NULL,'admin',NULL,0),(64,2,2,2,'CMS::Template::Version','/apis.json','apis.json','','{\r\n  \"name\": \"World Company\",\r\n  \"description\": \"This is an inventory of APIs available as part of the API World Company network.\",\r\n  \"image\": \"http://i.imgur.com/ntV92b4.png\",\r\n  \"tags\": [\r\n    \"Telecommunications\",\r\n    \"Communications\"\r\n  ],\r\n  \"created\": \"2017-01-24\",\r\n  \"modified\": \"2017-01-24\",\r\n  \"url\": \"http://worldcompany.com/apis.json\",\r\n  \"specificationVersion\": \"0.14\",\r\n  \"apis\": [\r\n    {\r\n      \"name\": \"SMS API\",\r\n      \"description\": \"Short Message Service (SMS) is a text messaging service component of most telephone, World Wide Web, and mobile telephony systems. It uses standardized communication protocols to enable mobile phone devices to exchange short text messages. We provide an API for it to let you send SMS to your customers.\",\r\n      \"X-3scale\":{\r\n        \"service_id\":1234,\r\n        \"swagger_system_name\":\"echo\"\r\n      },\r\n      \"image\": \"https://enterprisedemo.3scale.net/images/circle2.png\",\r\n      \"humanURL\": \"https://worldcompany.com/\",\r\n      \"baseURL\": \"http://sms.worldcompany.com\",\r\n      \"tags\": [\r\n        \"sms\",\r\n        \"text\"\r\n      ],\r\n      \"properties\": [\r\n        {\r\n          \"type\": \"X-signup\",\r\n          \"url\": \"https://worldcompany.com/\"\r\n        },\r\n        {\r\n          \"type\": \"Swagger\",\r\n          \"url\": \"https://worldcompany.com/swagger/spec/sms2_0.json\"\r\n        }\r\n      ],\r\n      \"contact\": [\r\n        {\r\n          \"FN\": \"World Company\",\r\n          \"email\": \"info@worldcompany.com\",\r\n         